stepDefinitionsTemplate.code:
package rimac.auto.web.stepdefinitions;

import io.cucumber.java.en.*;
import net.serenitybdd.screenplay.actors.OnStage;
import static net.serenitybdd.screenplay.actors.OnStage.theActorCalled;
import static net.serenitybdd.screenplay.actors.OnStage.theActorInTheSpotlight;

public class LoginStepDefinitions {

    @Given("{string} abre la página de login")
    public void abrePaginaLogin(String actor) {
        theActorCalled(actor).attemptsTo(
            AbrirPaginaLogin.go()
        );
    }

    @When("ingresa sus credenciales")
    public void ingresaCredenciales() {
        theActorInTheSpotlight().attemptsTo(
            IngresarCredenciales.con("usuario", "password")
        );
    }

    @Then("debe ver el dashboard")
    public void debeVerDashboard() {
        theActorInTheSpotlight().should(
            seeThat("El dashboard debe estar visible", DashboardVisibility.check(), equalTo(true))
        );
    }
}

